mapper.select:
SELECT dd.likeid, dd.postid, dd.memberid, po.travelid, po.content FROM tripflow.DDABONG dd LEFT JOIN tripflow.POST po ON dd.postid = po.postid
UNION ALL
SELECT dd.likeid, dd.postid, dd.memberid, po.travelid, po.content FROM tripflow.DDABONG dd RIGHT JOIN tripflow.POST po ON dd.postid = po.postid WHERE dd.postid IS NULL;
